import './App.css';

const App = () => {
  const [name, setName] = useState('');
  const [lastname, setLastname] = useState('');
  const [response, setResponse] = useState('');

  const sendJSON = () => {
    const url = 'http://mihailmaximov.ru/projects/json/json.php';
    const data = JSON.stringify({ name, lastname });

    fetch(url, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: data
    })
    .then(response => response.text())
    .then(data => {
      setResponse(data);
    })
    .catch(error => {
      console.error('Ошибка:', error);
    });
  };

  return (
    <div>
      <input type="text" value={name} onChange={e => setName(e.target.value)} placeholder="Ваше имя" />
      <input type="text" value={lastname} onChange={e => setLastname(e.target.value)} placeholder="и фамилия" />

      <button onClick={sendJSON}>Проверить JSON</button>

      <div>{response}</div>
    </div>
  );
};

export default App;
